mapper.select:
SELECT COUNT(DISTINCT A.id) FROM approval A JOIN employee B ON A.drafter_id = B.id JOIN department C ON B.dept_id = C.id LEFT JOIN department D ON C.parent_dept_id = D.id
JOIN approval_line E ON A.id = E.approval_id
A.drafter_id = #{employeeId}
AND E.approver_id = #{employeeId}
AND A.status = 'AS_APPR'
AND E.status IN
#{approvalLineStatus}
AND A.status = #{approvalStatus}
AND LEFT(A.drafted_at, 10) >= #{startDate}
AND #{endDate} >= LEFT(A.drafted_at, 10)
AND ( A.title LIKE CONCAT('%', #{keyword}, '%') OR B.name LIKE CONCAT('%', #{keyword}, '%') OR A.approval_code LIKE CONCAT('%', #{keyword}, '%') OR A.ref_code LIKE CONCAT('%', #{keyword}, '%') )
AND A.ref_code LIKE CONCAT(#{refType}, '%')
AND E.line_type = #{approvalLineType}
AND E.viewed_at IS NOT NULL
AND E.viewed_at IS NULL

SELECT A.id AS approvalId , A.approval_code AS approvalCode , A.title AS title , A.status AS approvalStatus , A.total_line AS totalLine , LEFT(A.ref_code, 2) AS refType , A.ref_code AS refCode , A.drafted_at AS draftedAt , A.drafter_id AS drafterId , B.name AS drafterName , B.position_code AS drafterPositionCode , B.rank_code AS drafterRankCode , C.dept_name AS drafterDepartment , D.dept_name AS drafterParentDepartment , CASE WHEN EXISTS ( SELECT 1 FROM approval_attachment F WHERE A.id = F.approval_id ) THEN true ELSE false END AS isApprovalAttachment
, E.viewed_at AS viewedAt
FROM approval A JOIN employee B ON A.drafter_id = B.id JOIN department C ON B.dept_id = C.id LEFT JOIN department D ON C.parent_dept_id = D.id
JOIN approval_line E ON A.id = E.approval_id
A.drafter_id = #{employeeId}
AND E.approver_id = #{employeeId}
AND A.status = 'AS_APPR'
AND E.status IN
#{approvalLineStatus}
AND A.status = #{approvalStatus}
AND LEFT(A.drafted_at, 10) >= #{startDate}
AND #{endDate} >= LEFT(A.drafted_at, 10)
AND ( A.title LIKE CONCAT('%', #{keyword}, '%') OR B.name LIKE CONCAT('%', #{keyword}, '%') OR A.approval_code LIKE CONCAT('%', #{keyword}, '%') OR A.ref_code LIKE CONCAT('%', #{keyword}, '%') )
AND A.ref_code LIKE CONCAT(#{refType}, '%')
AND E.line_type = #{approvalLineType}
AND E.viewed_at IS NOT NULL
AND E.viewed_at IS NULL
ORDER By A.id DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE
       approval_line A
   SET
       A.viewed_at = #{viewedAt}
 WHERE A.id = #{approvalLineId}
