mapper.select:
SELECT COUNT(*)
FROM work_order
WHERE work_date = #{workDate}

SELECT COUNT(DISTINCT wo.line_id)
FROM work_order wo
WHERE wo.work_date = #{workDate}
  AND wo.status = #{status}

SELECT pri.id AS prItemId, pri.pr_id AS prId,
soi.item_name AS itemName,
pri.quantity AS targetQty, pri.produced_quantity AS producedQty,
pl.line_name AS lineName,
m.id AS materialId,
/* 총 기간 (PR 생성일 ~ 납기일) */ DATEDIFF( STR_TO_DATE(pr.due_at, '%Y-%m-%d'), STR_TO_DATE(pr.created_at, '%Y-%m-%d') ) + 1 AS totalDays,
/* 경과 일수 (PR 생성일 ~ 오늘) */ GREATEST( DATEDIFF( CURDATE(), STR_TO_DATE(pr.created_at, '%Y-%m-%d') ) + 1, 0 ) AS elapsedDays
FROM production_request_item pri JOIN production_request pr ON pr.id = pri.pr_id
JOIN sales_order_item soi ON soi.id = pri.so_item_id
/* 생산계획 → 라인 */ LEFT JOIN production_plan pp ON pp.pr_item_id = pri.id AND pp.status = 'PP_CONFIRMED'
LEFT JOIN production_line pl ON pl.id = pp.production_line_id
/* 자재 */ JOIN material m ON m.id = pp.material_id
WHERE pr.status IN ('PR_APPR_DONE', 'PR_PLANNED', 'PR_PRODUCING')
