mapper.select:
SELECT /* 기본 출고 지시 정보 */ A.id AS gi_id, A.gi_code, A.gi_url, A.created_at, K.shipped_at AS scheduled_at, A.status,
/* 배송지 정보 (수주의 배송지 주소) */ C.company_name, B.address, D.name AS recipient_name, D.contact AS recipient_contact,
/* 관련 주문 정보 */ B.id AS so_id, B.so_code, K.id AS do_id, A.do_code, C.id AS client_id, C.company_name AS client_name, B.shipped_at,
/* 출고 창고 정보 */ E.id AS warehouse_id, E.name AS warehouse_name,
/* 특이사항 */ A.note,
/* 결재 정보 */ L.id AS approval_id, A.approval_code, F.id AS drafter_id, F.name AS drafter_name, U.dept_name AS drafter_department, V.name AS drafter_position, W.name AS drafter_rank, G.id AS manager_id, G.name AS manager_name, P.dept_name AS manager_department, Q.name AS manager_position, R.name AS manager_rank,
/* 품목 정보 */ CONCAT(A.gi_code, '-', LPAD(H.id, 3, '0')) AS gi_item_code, I.item_code, I.item_name, I.spec, H.quantity AS quantity_aun, (H.quantity * J.conversion_rate) AS quantity_bun, H.quantity AS total_quantity, I.unit,
/* 결재선 정보 */ M.id AS approval_line_id, N.name AS approver_name, O.dept_name AS approver_department, S.name AS approver_position, T.name AS approver_rank, M.sequence AS approval_sequence, M.status AS approval_status, M.line_type AS line_type, M.processed_at AS approval_processed_at, M.note AS approval_note,
/* 배송 정보 */ DL.tracking_number, DL.driver_name, DL.driver_contact, DL.departed_at, DL.arrived_at
FROM goods_issue A INNER JOIN sales_order B ON A.so_id = B.id INNER JOIN client C ON B.client_id = C.id LEFT JOIN client_employee D ON B.client_manager_id = D.id INNER JOIN warehouse E ON A.warehouse_id = E.id LEFT JOIN employee F ON A.drafter_id = F.id LEFT JOIN department U ON F.dept_id = U.id LEFT JOIN common_code V ON F.position_code = V.code LEFT JOIN common_code W ON F.rank_code = W.code LEFT JOIN employee G ON A.gi_manager_id = G.id LEFT JOIN department P ON G.dept_id = P.id LEFT JOIN common_code Q ON G.position_code = Q.code LEFT JOIN common_code R ON G.rank_code = R.code INNER JOIN goods_issue_item H ON A.id = H.gi_id INNER JOIN sales_order_item I ON H.so_item_id = I.id LEFT JOIN material J ON I.item_code = J.material_code INNER JOIN delivery_order K ON A.do_code = K.do_code LEFT JOIN approval L ON A.approval_code = L.approval_code LEFT JOIN approval_line M ON L.id = M.approval_id AND (M.line_type = 'AT_APPR' OR M.line_type = 'AT_RVW') LEFT JOIN employee N ON M.approver_id = N.id LEFT JOIN department O ON N.dept_id = O.id LEFT JOIN common_code S ON N.position_code = S.code LEFT JOIN common_code T ON N.rank_code = T.code LEFT JOIN delivery DL ON A.id = DL.gi_id
WHERE A.id = #{id} ORDER BY H.id, M.sequence
