mapper.select:
SELECT /* 기본 출고 지시 정보 */ A.gi_code, A.created_at, K.shipped_at AS scheduled_at, A.status,
/* 배송지 정보 (수주의 배송지 주소) */ C.company_name, B.address, D.name AS recipient_name, D.contact AS recipient_contact,
/* 관련 주문 정보 */ B.so_code, C.company_name AS client_name, B.shipped_at,
/* 출고 창고 정보 */ E.name AS warehouse_name,
/* 특이사항 */ A.note,
/* 결재 정보 */ A.approval_code, F.name AS drafter_name, G.name AS manager_name,
/* 품목 정보 */ CONCAT(A.gi_code, '-', LPAD(H.id, 3, '0')) AS gi_item_code, I.item_code, I.item_name, I.spec, H.quantity AS quantity_aun, (H.quantity * J.conversion_rate) AS quantity_bun, H.quantity AS total_quantity, I.unit
FROM goods_issue A INNER JOIN sales_order B ON A.so_id = B.id INNER JOIN client C ON B.client_id = C.id LEFT JOIN client_employee D ON B.client_manager_id = D.id INNER JOIN warehouse E ON A.warehouse_id = E.id INNER JOIN employee F ON A.drafter_id = F.id LEFT JOIN employee G ON A.gi_manager_id = G.id INNER JOIN goods_issue_item H ON A.id = H.gi_id INNER JOIN sales_order_item I ON H.so_item_id = I.id LEFT JOIN material J ON I.item_code = J.material_code INNER JOIN delivery_order K ON A.do_code = K.do_code
WHERE A.gi_code = #{giCode} ORDER BY H.id
